import React, { useEffect, useState } from "react";
import config from "./config.json";
import "./RoomStatus.css";

function RoomStatus() {
  const roomEmail = config.roomEmail;
  const roomName = config.roomName;
  const [events, setEvents] = useState([]);

  // Toplantı verilerini her 5 dakikada bir çek
  useEffect(() => {
    function fetchEvents() {
      const today = new Date();
      const year = today.getFullYear();
      const month = today.getMonth();
      const day = today.getDate();

      // Haftalık aralık
      const start = new Date(year, month, day, 0, 0, 0);
      const end = new Date(year, month, day + 7, 23, 59, 59);

      const apiUrl = `http://localhost:5079/room/events?roomEmail=${encodeURIComponent(roomEmail)}&start=${start.toISOString()}&end=${end.toISOString()}`;
      fetch(apiUrl)
        .then((res) => res.json())
        .then((data) => {
          const eventsArray = Array.isArray(data) ? data : (Array.isArray(data?.events) ? data.events : []);
          setEvents(eventsArray);
        })
        .catch((error) => {
          console.error("Veri çekme hatası:", error);
          setEvents([]);
        });
    }

    fetchEvents(); // ilk açılışta hemen çek
    const interval = setInterval(fetchEvents, 5 * 60 * 1000); // her 5 dakikada bir çek

    return () => clearInterval(interval); // component kapanınca timer'ı temizle
  }, [roomEmail]);

  // Bugünün tarihi (yyyy-MM-dd formatında)
  const todayStr = new Date().toISOString().slice(0, 10);
  const todaysEvents = events.filter(e => e.date === todayStr);
  const futureEvents = events.filter(e => e.date !== todayStr);

  return (
    <div>
      <h2>{roomName}</h2>
      <h3>Bugünkü Toplantılar</h3>
      <table className="meeting-table">
        <thead>
          <tr>
            <th>Tarih</th>
            <th>Başlangıç</th>
            <th>Bitiş</th>
            <th>Konu</th>
            <th>Durum</th>
          </tr>
        </thead>
        <tbody>
          {!todaysEvents || todaysEvents.length === 0 ? (
            <tr>
              <td colSpan="5">Bugün için toplantı yok</td>
            </tr>
          ) : (
            todaysEvents.map((event) => (
              <tr key={event.id}>
                <td>{event.date}</td>
                <td>{event.start ? new Date(event.start).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                <td>{event.end ? new Date(event.end).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                <td>{event.subject || "-"}</td>
                <td>
                  <span className="badge badge-danger">Dolu</span>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {futureEvents.length > 0 && (
        <>
          <h3 style={{ marginTop: "30px" }}>Rezerve Edilmiş Toplantılar</h3>
          <table className="meeting-table">
            <thead>
              <tr>
                <th>Tarih</th>
                <th>Başlangıç</th>
                <th>Bitiş</th>
                <th>Konu</th>
                <th>Durum</th>
              </tr>
            </thead>
            <tbody>
              {futureEvents.map((event) => (
                <tr key={event.id}>
                  <td>{event.date}</td>
                  <td>{event.start ? new Date(event.start).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                  <td>{event.end ? new Date(event.end).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                  <td>{event.subject || "-"}</td>
                  <td>
                    <span className="badge badge-danger">Dolu</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default RoomStatus;
