import React, { useEffect, useState } from "react";
import config from "./config.json";
import "./RoomStatus.css";

function RoomStatus() {
  const roomEmail = config.roomEmail;
  const [room, setRoom] = useState(null);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Oda bilgilerini çek
    fetch("http://localhost:5079/room/rooms")
      .then((res) => res.json())
      .then((rooms) => {
        const found = rooms.find(r => r.email === roomEmail);
        setRoom(found);
      });

    // Türkiye saatine göre günün başlangıcı ve bitişi
    const today = new Date();
    const start = new Date(today.getFullYear(), today.getMonth(), today.getDate(), 0, 0, 0);
    const end = new Date(today.getFullYear(), today.getMonth(), today.getDate(), 23, 59, 59);
    // UTC'ye çevirmek için 3 saat geri al
    start.setHours(start.getHours() - 3);
    end.setHours(end.getHours() - 3);
    const apiUrl = `http://localhost:5079/room/events?roomEmail=${encodeURIComponent(roomEmail)}&start=${start.toISOString()}&end=${end.toISOString()}`;

    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => setEvents(data));
  }, [roomEmail]);

  return (
    <div>
      <h2>{room ? room.name : "Oda Bilgisi Yükleniyor..."}</h2>
      <div style={{ marginBottom: "10px", color: "#888" }}>
        <b>Oda e-posta:</b> {roomEmail}
      </div>
      <h3>Bugünkü Toplantılar</h3>
      <table>
        <thead>
          <tr>
            <th>Başlangıç</th>
            <th>Bitiş</th>
            <th>Konu</th>
            <th>Durum</th>
          </tr>
        </thead>
        <tbody>
          {events.length === 0 ? (
            <tr>
              <td colSpan="4">Bugün için toplantı yok</td>
            </tr>
          ) : (
            events.map((event) => (
              <tr key={event.id}>
                <td>{event.start ? new Date(event.start).toLocaleTimeString() : "-"}</td>
                <td>{event.end ? new Date(event.end).toLocaleTimeString() : "-"}</td>
                <td>{event.subject || "-"}</td>
                <td><span className="badge badge-danger">Dolu</span></td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default RoomStatus;
