import React, { useEffect, useState } from "react";
import "./RoomStatus.css"; // Stil için

function RoomStatus() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetch("https://localhost:5001/room") // Kendi API adresini yaz
      .then((res) => res.json())
      .then((data) => setEvents(data));
  }, []);

  return (
    <div>
      <h2>Oda Müsaitliği</h2>
      <table>
        <thead>
          <tr>
            <th>Durum</th>
            <th>Toplantı Odası</th>
            <th>Başlangıç Tarihi</th>
            <th>Bitiş Tarihi</th>
            <th>Oda Durumu</th>
          </tr>
        </thead>
        <tbody>
          {events.length === 0 ? (
            <tr>
              <td colSpan="5">Kayıt yok</td>
            </tr>
          ) : (
            events.map((event) => (
              <tr key={event.id}>
                <td>
                  <span className="badge badge-success">Onaylandı</span>
                </td>
                <td>{event.location?.displayName || "Oda Bilgisi Yok"}</td>
                <td>{new Date(event.start.dateTime).toLocaleString()}</td>
                <td>{new Date(event.end.dateTime).toLocaleString()}</td>
                <td>
                  <span className="badge badge-danger">Dolu</span>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default RoomStatus;
