import React, { useEffect, useState } from "react";
import config from "./config.json";
import "./RoomStatus.css";

function RoomStatus() {
  const roomEmail = config.roomEmail;
  const [room, setRoom] = useState(null);
  const [events, setEvents] = useState([]);

  // Oda bilgisi bir kez çekilsin
  useEffect(() => {
    fetch("http://localhost:5079/room/rooms")
      .then((res) => res.json())
      .then((rooms) => {
        const found = rooms.find(r => r.email === roomEmail);
        setRoom(found);
      });
  }, [roomEmail]);

  // Toplantı verilerini her 5 dakikada bir çek
  useEffect(() => {
    function fetchEvents() {
      const today = new Date();
      const year = today.getFullYear();
      const month = today.getMonth();
      const day = today.getDate();

      // Türkiye saatiyle 07:30 ve 18:30
      const start = new Date(year, month, day, 10, 30, 0);
      const end = new Date(year, month, day, 21, 30, 0);

      const apiUrl = `http://localhost:5079/room/events?roomEmail=${encodeURIComponent(roomEmail)}&start=${start.toISOString()}&end=${end.toISOString()}`;
      fetch(apiUrl)
        .then((res) => res.json())
        .then((data) => setEvents(data));
    }

    fetchEvents(); // ilk açılışta hemen çek
    const interval = setInterval(fetchEvents, 5 * 60 * 1000); // her 5 dakikada bir çek

    return () => clearInterval(interval); // component kapanınca timer'ı temizle
  }, [roomEmail]);

  return (
    <div>
      <h2>{room ? room.name : "Oda Bilgisi Yükleniyor..."}</h2>
      <div style={{ marginBottom: "10px", color: "#888" }}>
        <b>Oda e-posta:</b> {roomEmail}
      </div>
      <h3>Bugünkü Toplantılar</h3>
      <table className="meeting-table">
        <thead>
          <tr>
            <th>Başlangıç</th>
            <th>Bitiş</th>
            <th>Konu</th>
            <th>Durum</th>
          </tr>
        </thead>
        <tbody>
          {events.length === 0 ? (
            <tr>
              <td colSpan="4">Bugün için toplantı yok</td>
            </tr>
          ) : (
            events.map((event) => (
              <tr key={event.id}>
                <td>{event.start ? new Date(event.start).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                <td>{event.end ? new Date(event.end).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "-"}</td>
                <td>{event.subject || "-"}</td>
                <td>
                  <span className="badge badge-danger">Dolu</span>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default RoomStatus;
