import React, { useEffect, useState } from "react";
// import config from "./config.json";
import "./RoomDisplay.css";

function RoomDisplay() {
  const [roomName, setRoomName] = useState("");
  const [roomEmail, setRoomEmail] = useState("");
  const [events, setEvents] = useState([]);
  const [now, setNow] = useState(new Date());

  // Oda bilgisini backend'den çek
  useEffect(() => {
    fetch("http://localhost:5079/room/rooms")
      .then(res => res.json())
      .then(data => {
        const activeRoom = Array.isArray(data) ? data.find(r => r.isActive) : null;
        if (activeRoom) {
          setRoomName(activeRoom.name);
          setRoomEmail(activeRoom.email);
        }
      });
  }, []);

  // Toplantı verilerini çek
  useEffect(() => {
    if (!roomEmail) return;
    function fetchEvents() {
      const today = new Date();
      const year = today.getFullYear();
      const month = today.getMonth();
      const day = today.getDate();
      const start = new Date(year, month, day, 0, 0, 0);
      const end = new Date(year, month, day + 1, 23, 59, 59);
      const apiUrl = `${apiBase}/room/events?roomEmail=${encodeURIComponent(roomEmail)}&start=${start.toISOString()}&end=${end.toISOString()}`;
      fetch(apiUrl)
        .then((res) => res.json())
        .then((data) => {
          const eventsArray = Array.isArray(data) ? data : (Array.isArray(data?.events) ? data.events : []);
          setEvents(eventsArray);
        })
        .catch(() => setEvents([]));
    }
    fetchEvents();
    const interval = setInterval(fetchEvents, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, [roomEmail]);

  useEffect(() => {
    const timer = setInterval(() => setNow(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  // Şu anki toplantı ve sonraki toplantılar
  const nowTime = now.getTime();
  const todayStr = now.toISOString().slice(0, 10);
  let current = null;
  let nextEvents = [];
  if (events.length > 0) {
    // Bugünkü ve bugünden sonraki toplantıları ayır
    const todaysEvents = events.filter(ev => ev.date === todayStr);
    const futureEvents = events.filter(ev => ev.date > todayStr);
    // Solda gösterilecek toplantı: şu an devam eden veya bugünkü ilk toplantı
    for (let i = 0; i < todaysEvents.length; i++) {
      const ev = todaysEvents[i];
      const start = new Date(ev.start.replace('T', ' ')).getTime();
      const end = new Date(ev.end.replace('T', ' ')).getTime();
      if (nowTime >= start && nowTime < end) {
        current = ev;
        break;
      }
      if (nowTime < start && !current) {
        current = ev;
        break;
      }
    }
    // Sağda gösterilecek toplantılar: bugünkü saatten sonra başlayacaklar + gelecektekiler
    nextEvents = [
      ...todaysEvents.filter(ev => {
        const start = new Date(ev.start.replace('T', ' ')).getTime();
        return start > nowTime;
      }),
      ...futureEvents
    ];
  }

  const isBusy = !!current;

  return (
    <div className="room-display-flex">
      <div className="room-display-main">
        <div className="room-title">{roomName}</div>
        <div className="room-main">
          {current ? (
            <>
              <div className="meeting-subject">KONUSU: {current.subject}</div>
              <div className="meeting-organizer">Düzenleyen: {current.organizer}</div>
              <div className="meeting-time">
                {new Date(current.start.replace('T', ' ')).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                {" - "}
                {new Date(current.end.replace('T', ' ')).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </>
          ) : (
            <div className="meeting-free">Şu anda toplantı yok</div>
          )}
        </div>
      </div>
      <div className="room-next-green">
        <div className="room-clock room-clock-green">{now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
        <div className="next-content">
          <div className="next-title-green">PLANLANMIŞ TOPLANTILAR</div>
          {nextEvents.length === 0 ? (
            <div className="no-next">Bu hafta planlanmış başka toplantı yok</div>
          ) : (
            <ul>
              {nextEvents.map(ev => (
                <li key={ev.id}>
                  <div className="next-date">{new Date(ev.start.replace('T', ' ')).toLocaleDateString('tr-TR', { year: 'numeric', month: '2-digit', day: '2-digit' })}</div>
                  <div className="next-row">
                    <span className="next-subject">{ev.subject} Toplantısı</span>
                    <span className="next-time">
                      {new Date(ev.start.replace('T', ' ')).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      {" - "}
                      {new Date(ev.end.replace('T', ' ')).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </span>
                  </div>
                  <div className="next-organizer">{ev.organizer}</div>
                </li>
              ))}
            </ul>
          )}
        </div>
        {/* Oda durumu kutusu */}
        {(() => {
          // Bugünkü toplantıların bitişine göre durum
          const today = new Date();
          const todayStr = today.toISOString().slice(0, 10);
          const nowTime = today.getTime();
          const todaysEvents = events.filter(e => e.date === todayStr);
          let isBusy = false;
          let isFinished = true;
          for (let i = 0; i < todaysEvents.length; i++) {
            const ev = todaysEvents[i];
            const start = new Date(ev.start.replace('T', ' ')).getTime();
            const end = new Date(ev.end.replace('T', ' ')).getTime();
            if (nowTime >= start && nowTime < end) {
              isBusy = true;
              isFinished = false;
              break;
            }
            if (nowTime < end) {
              isFinished = false;
            }
          }
          if (isBusy) {
            return <div className="room-status-box-centered busy">DOLU</div>;
          } else if (isFinished) {
            return <div className="room-status-box-centered free">MÜSAİT</div>;
          } else {
            return <div className="room-status-box-centered free">MÜSAİT</div>;
          }
        })()}
      </div>
    </div>
  );
}

export default RoomDisplay;
